function [] = HRdisp(ts,xs,posT)% HRdisp.m%% Display current status%% Revision history% 150520 Created% 150619 Graph size and axis limits are modified.% 150619 Figure setting is changed. (For 2015a)% 150619 Pause function is called. (For 2015a)%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%global x_ t_global r_HR h_HR l_HR alphaglobal STATE_A STATE_B STATE_C STATE_D VXA_MINglobal N_pos N_the N_vel N_omg global N_stateglobal TARGET_WIDTH TARGET_PERIODtmin = min(ts); tmax = max(ts);%%%graph_siz = [floor(630*1024/1920),floor(410*640/1080)];graph_pos = [10+graph_siz(1)*[0,1,2,0,1,2];...             floor((580-64)*640/1080)+[64,64,64,64,64,64]*(-1)]';%%%pos		= x_(N_pos);the		= x_(N_the);state	= x_(N_state);posA	= pos + l_HR*[ cos(the+alpha);-sin(the+alpha)];posB	= pos - l_HR*[ cos(the-alpha);-sin(the-alpha)];posC	= pos - l_HR*[ cos(the+alpha);-sin(the+alpha)];posD	= pos + l_HR*[ cos(the-alpha);-sin(the-alpha)];xlimx1	= min([0.0,posT(1)])-0.2;xlimx2	= max([0.0,posT(1)])+0.2;ylimy1 = -0.1;ylimy2 = abs(posT(1))/2+0.1;h = figure(101);set(h,'Position',[graph_pos(101-100,[1,2]),graph_siz*2]);clfff1=[xlimx1,xlimx2,xlimx2,xlimx1,xlimx1];ff2=[   0,  0,-0.2,-0.2,   0];fill(ff1,ff2,[0.7,0.7,0.7]);	hold onplot(posT(1)+TARGET_WIDTH/2*[-1,1],[0,0],'r-.','LineWidth',2.0);plot([posA(1),posB(1)],[posA(2),posB(2)],'b-','LineWidth',4.0);hold onplot([posB(1),posC(1)],[posB(2),posC(2)],'k-','LineWidth',2.0);plot([posC(1),posD(1)],[posC(2),posD(2)],'k-','LineWidth',2.0);plot([posD(1),posA(1)],[posD(2),posA(2)],'k-','LineWidth',2.0);xlim([xlimx1,xlimx2]);ylim([ylimy1,ylimy2]);set(gcf,'doublebuffer','on');title(sprintf('Rover state is %s at t=%6.2f',char(64+state),t_));xlabel('X (horizontal) [m]');ylabel('Y (vertical) [m]');axis('equal');pause(0.01);